import React, { Fragment, useState } from 'react';
import NumberFormat from 'react-number-format';
import {
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Alert
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

const AddContactsForm = props => {
  const [formData, setFormData] = useState({
    Name: {
      value: '',
      validation: {
        required: true,
        maxLength: 30
      }
    },
    Phone: {
      value: '',
      required: true,
      isNumber: true,
      phonFomat: true
    },
    Title: {
      value: '',
      required: true
    }
  });
  const [dropdownOpen, setOpen] = useState(false);

  const toggle = () => setOpen(!dropdownOpen);

  const { Name, Phone, Title } = formData;
  const onChange = e => {
    console.log([e.target.name]);

    [e.target.name].valid = checkValidity(e.target.value, {
      ...e.target.validation
    });
    console.log({ ...formData.value });
    setFormData({ ...formData.value, [e.target.name]: e.target.value });
  };
  const checkValidity = (value, rules) => {
    let isValid = true;
    console.log('ENTER TO FUNC');
    console.log(rules);

    if (rules.required) {
      console.log('empty');

      isValid = value.trim() !== '' && isValid;
    }

    if (rules.minLength) {
      console.log('leng');
      isValid = value.length >= rules.minLength && isValid;
    }

    if (rules.maxLength) {
      isValid = value.length <= rules.maxLength && isValid;
    }

    return isValid;
  };
  const loginHandler = async e => {
    e.preventDefault();
    ///if some of the input is wrong we alert to user
    ///TO-DO
  };

  return (
    <Fragment>
      <form>
        <div className='new-contact-input'>
          <label>Name</label>
          <input
            onChange={e => onChange(e)}
            required
            name='Name'
            type='text'
            value={Name.value}
            valid={false}
          />
        </div>
        <div className='new-contact-input'>
          <label>Phone</label>

          <input
            onChange={e => onChange(e)}
            required
            name='Phone'
            value={Phone.value}
            type='text'
            validation={{ required: true, minLength: 3 }}
            valid={false}
          />
        </div>
        <div className='new-contact-input'>
          <label>Title</label>
          <input />
        </div>
      </form>
    </Fragment>
  );
};

export default AddContactsForm;
