import React, { Fragment, useState } from 'react';
import NumberFormat from 'react-number-format';
import {
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Alert
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

const AddContactsForm = props => {
  const [formData, setFormData] = useState({
    Name: {
      value: '',
      validation: {
        required: true,
        maxLength: 30
      }
    },
    Phone: {
      value: '',
      validation: {
        required: true,
        isNumber: true,
        phonFormat: true
      }
    },
    Title: {
      value: '',
      required: true
    }
  });
  const [dropdownOpen, setOpen] = useState(false);

  const toggle = () => setOpen(!dropdownOpen);

  const { Name, Phone, Title, validation } = formData;
  const onChange = e => {
    // console.log([e.target.name]);
    // [e.target.name].valid = checkValidity(e.target.value, {
    //   ...e.target.validation
    // });
    console.log(e.target.value);
    console.log(e.target.name);
    console.log(formData['e.target.name']);

    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };
  const checkValidity = (value, rules) => {
    let isValid = true;
    console.log('ENTER TO FUNC');
    console.log(rules);

    if (rules.required) {
      console.log('empty');

      isValid = value.trim() !== '' && isValid;
    }

    if (rules.minLength) {
      console.log('leng');
      isValid = value.length >= rules.minLength && isValid;
    }

    if (rules.maxLength) {
      isValid = value.length <= rules.maxLength && isValid;
    }

    return isValid;
  };
  const loginHandler = async e => {
    e.preventDefault();
    ///if some of the input is wrong we alert to user
    ///TO-DO
  };

  return (
    <Fragment>
      <form>
        <div className='new-contact-input'>
          <label>Name</label>
          <input
            value={Name.value}
            onChange={e => onChange(e)}
            required
            name='Name'
            type='text'
            valid={false}
          />
        </div>
        <div className='new-contact-input'>
          <label>Phone</label>

          <input
            value={Phone.value}
            onChange={e => onChange(e)}
            required
            name='Phone'
            type='text'
            valid={false}
          />
        </div>
        <div className='new-contact-input'>
          <label>Title</label>
          <input />
        </div>
      </form>
    </Fragment>
  );
};

export default AddContactsForm;
