import React, { Fragment, useState } from 'react';
import NumberFormat from 'react-number-format';
import {
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Alert
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

const AddContactsForm = props => {
  const [formData, setFormData] = useState({
    Name: '',
    Phone: '',

    Title: ''
  });

  const { Name, Phone, Title, validation } = formData;
  const onChange = e => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const checkValidity = (value, rules) => {
    let isValid = true;
    console.log('ENTER TO FUNC');
    console.log(rules);

    if (rules.required) {
      console.log('empty');

      isValid = value.trim() !== '' && isValid;
    }

    if (rules.minLength) {
      console.log('leng');
      isValid = value.length >= rules.minLength && isValid;
    }

    if (rules.maxLength) {
      isValid = value.length <= rules.maxLength && isValid;
    }

    return isValid;
  };
  const RegisterHandler = async e => {
    e.preventDefault();
    console.log(Name);

    ///if some of the input is wrong we alert to user
    ///TO-DO
  };

  return (
    <Fragment>
      <div className='new-contact-inputs'>
        <form>
          <div className='new-contact-input'>
            <label>Name</label>
            <input
              value={Name.value}
              onChange={e => onChange(e)}
              required
              name='Name'
              type='text'
            />
          </div>
          <div className='new-contact-input'>
            <label>Phone</label>

            <input
              value={Phone.value}
              onChange={e => onChange(e)}
              required
              name='Phone'
              type='text'
            />
          </div>
          <div className='new-contact-input'>
            <label>Title</label>
            <input />
          </div>
          <div className='new-contact-buttons'>
            <button
              onClick={e => RegisterHandler(e)}
              type='submit'
              className='button-ok'
            >
              Save
            </button>
            <button
              onClick={e => RegisterHandler(e)}
              type='submit'
              className='button-ok'
              className='button-cancel'
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </Fragment>
  );
};

export default AddContactsForm;
