import React, { Component, Fragment } from 'react';
import { getContent } from '../../action/contact';
import '../contract/Contact.css';
import { setImage } from '../../action/setImage';
import Imgrandom from '../../components/Imgrandom';
import { connect } from 'react-redux';
import AddContactsForm from '../../components/AddContactsForm';

class NewContact extends Component {
  state = {
    imgUrl: '',
    isLoading: true,
    isRedirect: false,
    disableButton: false
  };

  componentDidMount = () => {
    if (this.props.match.params.id !== null && this.props.match.params.id) {
      this.props.getContent(this.props.match.params.id);
      this.setState({ ...this.state, imgUrl: contant.avatar });
    } else {
      this.props.setImage();
    }
  };
  changeImgHandler = () => {
    this.props.setImage();
  };
  render() {
    return (
      <Fragment>
        <div className='contact-container'>
          <div className='new-contact-container'>
            <div className='new-contact-avatar'>
              <Imgrandom></Imgrandom>
              <button onClick={() => this.changeImgHandler()}>
                <i className='fa fa-refresh' aria-hidden='true'></i>
              </button>
            </div>

            <AddContactsForm />
          </div>
        </div>
      </Fragment>
    );
  }
}
const mapStateToProps = state => ({
  contant: state.contactMangment.contact
});

export default connect(mapStateToProps, { setImage, getContent })(NewContact);
