import {
  GET_CONTACT,
  GET_ALL_CONTACT,
  REMOVE_CONTACT,
  SET_CONTACT,
  SET_CONTACT_ERROR
} from '../action/type';

const initialState = {
  contact: null,
  contacts: [],
  loading: true,
  error: {}
};

export default (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case SET_CONTACT:
      return {
        ...state,
        contact: payload,
        contacts: [...contacts, payload],
        loading: false
      };
    case SET_CONTACT_ERROR:
      return {
        ...state,
        error: payload,
        contact: null,
        loading: false
      };

    default:
      return state;
  }
};
