import React, { Fragment, useState } from 'react';
import NumberFormat from 'react-number-format';
import {
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Alert
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

const AddContactsForm = props => {
  const [formData, setFormData] = useState({
    Name: '',
    Phone: '',
    Title: ''
  });
  const errorMsg = [];
  const { Name, Phone, Title, validation } = formData;
  const onChange = e => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const pushErrorMassage = (isValid, msg) => {
    if (isValid) {
      errorMsg.push(msg);
    }
  };
  const checkValidity = (Name, Phone, Title) => {
    let isValid = true;

    isValid = Name.trim() !== '' && isValid;
    pushErrorMassage(isValid, 'Name is Requierd');
    isValid = Phone.trim() !== '' && isValid;
    pushErrorMassage(isValid, 'Phone is Requierd');
    isValid = Title.trim() !== '' && isValid;
    pushErrorMassage(isValid, 'Title is Requierd');

    isValid = Phone.length <= 30 && isValid;
    pushErrorMassage(isValid, 'Phone is Requierd');
    return isValid;
  };

  const RegisterHandler = async e => {
    e.preventDefault();
    if (!checkValidity(Name, Phone, Title)) {
    }
  };

  return (
    <Fragment>
      <div className='new-contact-inputs'>
        <form>
          <div className='new-contact-input'>
            <label>Name</label>
            <input
              value={Name.value}
              onChange={e => onChange(e)}
              required
              name='Name'
              type='text'
            />
          </div>
          <div className='new-contact-input'>
            <label>Phone</label>

            <input
              value={Phone.value}
              onChange={e => onChange(e)}
              required
              name='Phone'
              type='text'
            />
          </div>
          <div className='new-contact-input'>
            <label>Title</label>
            <input />
          </div>
          <div className='new-contact-buttons'>
            <button
              onClick={e => RegisterHandler(e)}
              type='submit'
              className='button-ok'
            >
              Save
            </button>
            <button
              onClick={e => RegisterHandler(e)}
              type='submit'
              className='button-ok'
              className='button-cancel'
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </Fragment>
  );
};

export default AddContactsForm;
