import React, { useEffect, useState } from 'react';
import { removeContact } from '../action/contact';
import { connect } from 'react-redux';
import '../pages/contract/Contact.css';
const ShowContact = props => {
  let [showAll, setCount] = useState(0);
  const onDeleteContentHandler = (e, id) => {
    props.removeContact(id);
  };

  useEffect(() => {
    showAll = props.contacts.map(el => {
      return (
        <div className='contact' key={el.id}>
          <div className='contact-avatar'>
            <img src={el.avatar} />
          </div>
          <div className='contact-details'>
            <div className='contact-name'>{el.name}</div>
            <div className='contact-phone'>{el.phone}</div>
          </div>
          <div className='contact-buttons'>
            <button>
              <i className='fa fa-phone' aria-hidden='true'></i>
            </button>
          </div>
          <div
            onClick={e => onDeleteContentHandler(e, el.id)}
            className='contact-button-close'
          >
            <i className='fa fa-times' aria-hidden='true'></i>
          </div>
        </div>
      );
    });
  });

  return <div>{showAll}</div>;
};
const mapStateToProps = state => ({
  contacts: state.contactMangment.contacts,
  loading: state.contactMangment.loading
});

export default connect(mapStateToProps, { removeContact })(ShowContact);
