import React, { Fragment, useState } from 'react';
import NumberFormat from 'react-number-format';
import {
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Alert
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

const AddContactsForm = props => {
  const [formData, setFormData] = useState({
    Name: '',
    Phone: '',
    Title: ' '
  });
  const [dropdownOpen, setOpen] = useState(false);

  const toggle = () => setOpen(!dropdownOpen);

  const { Name, Phone, Title } = formData;
  const onChange = e => {
    if (e.target.name === 'Phone') {
      [e.target.name] = e.target.validity.valid ? e.target.value : 'null';
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };
  const checkValidity = (value, rules) => {
    let isValid = true;

    if (rules.required) {
      isValid = value.trim() !== '' && isValid;
    }

    if (rules.minLength) {
      isValid = value.length >= rules.minLength && isValid;
    }

    if (rules.maxLength) {
      isValid = value.length <= rules.maxLength && isValid;
    }

    return isValid;
  };
  const loginHandler = async e => {
    e.preventDefault();
    ///if some of the input is wrong we alert to user
    ///TO-DO
  };

  return (
    <Fragment>
      <form>
        <div className='new-contact-input'>
          <label>Name</label>
          <input
            onChange={e => onChange(e)}
            required
            name='Name'
            type='text'
            value={Name}
          />
        </div>
        <div className='new-contact-input'>
          <label>Phone</label>

          <input
            onChange={e => onChange(e)}
            required
            name='Phone'
            value={Phone}
            type='text'
            validation={{ required: true, minLength }}
          />
        </div>
        <div className='new-contact-input'>
          <label>Title</label>
          <input />
        </div>
      </form>
    </Fragment>
  );
};

export default AddContactsForm;
