import {
  GET_CONTACT,
  GET_ALL_CONTACT,
  REMOVE_CONTACT,
  SET_CONTACT,
  SET_CONTACT_ERROR
} from './type';
import axios from 'axios';
export const setContact = ({ contact }) => async dispatch => {
  try {
    console.log('ACTION');
    console.log(contact);

    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    };

    const body = JSON.stringify(contact);

    const res = await axios.post('/api/contacts', body, config);
    dispatch({
      type: SET_CONTACT,
      payload: res.data
    });
  } catch (error) {
    dispatch({
      type: SET_CONTACT_ERROR,
      payload: error
    });
  }
};
