import React, { Fragment, useState } from 'react';
import { setAlert } from '../action/setAlert';
import { connect } from 'react-redux';
import NumberFormat from 'react-number-format';
import AlertOn from '../Layout/AlertOn';
import 'bootstrap/dist/css/bootstrap.css';

const AddContactsForm = props => {
  const [formData, setFormData] = useState({
    Name: '',
    Phone: '',
    Title: ''
  });
  const errorMsg = [];
  const { Name, Phone, Title, validation } = formData;
  const onChange = e => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const pushErrorMassage = msg => {
    errorMsg.push(msg);
    return false;
  };
  const checkValidity = (Name, Phone, Title) => {
    let isValid = true;

    isValid = Name.trim() !== '';
    if (isValid) pushErrorMassage('Name is Requierd');
    isValid = Phone.trim() !== '';
    if (isValid) pushErrorMassage('Phone is Requierd');
    isValid = Title.trim() !== '';
    if (isValid) pushErrorMassage('Title is Requierd');
    isValid = Phone.length <= 30;
    if (isValid) pushErrorMassage('Phone Number Cant be more 30 Numbers');
    const phoneno = /[0-9()-]+/;
    isValid = Phone.match(phoneno);
    if (isValid) pushErrorMassage('Iligal phone format');

    isValid = errorMsg.length > 0 ? true : false;
    return isValid;
  };

  const RegisterHandler = async e => {
    console.log('ENTER TO REG');

    e.preventDefault();
    if (!checkValidity(Name, Phone, Title)) {
      props.setAlert(errorMsg);
    }
  };

  return (
    <Fragment>
      <div className='new-contact-inputs'>
        <form>
          <div className='new-contact-input'>
            <label>Name</label>
            <input
              value={Name.value}
              onChange={e => onChange(e)}
              required
              name='Name'
              type='text'
            />
          </div>
          <div className='new-contact-input'>
            <label>Phone</label>

            <input
              value={Phone.value}
              onChange={e => onChange(e)}
              required
              name='Phone'
              type='text'
            />
          </div>
          <div className='new-contact-input'>
            <label>Title</label>
            <input />
          </div>
          <div className='new-contact-buttons'>
            <button
              onClick={e => RegisterHandler(e)}
              type='submit'
              className='button-ok'
            >
              Save
            </button>
            <button
              onClick={e => RegisterHandler(e)}
              type='submit'
              className='button-ok'
              className='button-cancel'
            >
              Cancel
            </button>
          </div>
        </form>
        <AlertOn />
      </div>
    </Fragment>
  );
};

export default connect()(AddContactsForm);
