import React, { Component, Fragment } from 'react';
import cors from 'cors';
import '../contract/Contact.css';
import axios from 'axios';
import { setImage } from '../../action/setImage';
import Imgrandom from '../../Layout/Imgrandom';
import { connect } from 'react-redux';

const MIN = 1;
const MAX = 100;
class NewContact extends Component {
  state = {
    imgUrl: '',
    isLoading: true
  };

  componentDidMount = async () => {
    // console.log('CONPONENT DID MOUNT');

    // const min = 1;
    // const max = 100;
    // const rand = MIN + Math.floor(Math.random() * (MAX - MIN));

    // try {
    //   this.setState({
    //     imgUrl: `https://randomuser.me/api/portraits/men/${rand}.jpg`,
    //     isLoading: false
    //   });
    // } catch (error) {
    //   this.setState({
    //     ...this.state,
    //     isLoading: false
    //   });
    //   console.log('ERROR==>>>>>>>' + error);
    // }
    this.props.setImage();
  };
  render() {
    // let img = this.state.isLoading ? (
    //   <Fragment>
    //     <p>Loading</p>
    //   </Fragment>
    // ) : (
    //   <img src={this.state.imgUrl} />
    // );
    return (
      <Fragment>
        <div className='contact-container'>
          <div className='new-contact-container'>
            <div className='new-contact-avatar'>
              <Imgrandom></Imgrandom>
              <button>
                <i className='fa fa-refresh' aria-hidden='true'></i>
              </button>
            </div>
            <div className='new-contact-inputs'>
              <div className='new-contact-input'>
                <label>Name</label>
                <input />
              </div>
              <div className='new-contact-input'>
                <label>Phone</label>
                <input />
              </div>
              <div className='new-contact-input'>
                <label>Title</label>
                <input />
              </div>
            </div>
            <div className='new-contact-buttons'>
              <button className='button-ok'>Save</button>
              <button className='button-cancel'>Cancel</button>
            </div>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default connect(null, { setImage })(NewContact);
