import React, { Component, Fragment } from 'react';
import { getAllContact } from '../../action/contact';
import ShowContact from '../../components/ShowContact';
import { connect } from 'react-redux';
import LoadingSpinner from '../../Layout/LoadingSpinner/LoadingSpinner';
import { Redirect } from 'react-router-dom';

import './Contact.css';
class Contact extends Component {
  state = {
    loading: '',
    isRedirect: false
  };
  componentDidMount = async () => {
    await this.props.getAllContact();
  };
  redirectToAddhandler = e => {
    console.log('HERE');

    this.setState({ ...this.state, isRedirect: true });
  };
  serchHandler = e => {
    console.log(e.currentTarget.value);

    if (typeof e.currentTarget.value[0] === 'string') {
      let con = this.props.contacts.filter(
        suggestion =>
          suggestion.name
            .toLowerCase()
            .indexOf(e.currentTarget.value.toLowerCase()) > -1
      );
      if (typeof e.currentTarget.value[0] === 'number') {
        let con = this.props.contacts.filter(
          suggestion => suggestion.startsWith(e.currentTarget.value) > -1
        );
        console.log(con);
      }

      //console.log(con);
    }
  };
  randomContectClickHandler = e => {};

  render() {
    return this.props.isLoading ? (
      <LoadingSpinner></LoadingSpinner>
    ) : this.state.isRedirect ? (
      <Redirect to='/contatcs/new' />
    ) : (
      <Fragment>
        <div className='body'>
          <div className='contact-container'>
            <div className='search-input'>
              <input
                onChange={e => this.serchHandler(e)}
                type='text'
                name='userInput'
                placeholder='search in contacts...'
              />
              <div className='search-icon'>
                <i className='fa fa-search' aria-hidden='true'></i>
              </div>
            </div>
            <div className='contacts-container'>
              <ShowContact />
            </div>
            <div className='contact-new'>
              <button>
                <i
                  onClick={e => this.redirectToAddhandler(e)}
                  className='fa fa-user-plus'
                  aria-hidden='true'
                ></i>
              </button>
              <button onClick={e => this.randomContectClickHandler}>
                <i
                  className='fa fa-random'
                  aria-hidden='true'
                  style={{ marginLeft: '15px' }}
                ></i>
              </button>
            </div>
          </div>
        </div>
      </Fragment>
    );
  }
}
const mapStateToProps = state => ({
  isLoading: state.contactMangment.loading,
  contacts: state.contactMangment.contacts
});

export default connect(mapStateToProps, { getAllContact })(Contact);
